(463, 1, '2022-07-06 12:29:47', '2022-07-06 09:29:47', '<!-- wp:cover {\"url\":\"http://localhost:8888/promart/wp-content/uploads/2022/07/promart-main-bg-scaled.jpeg\",\"id\":361,\"dimRatio\":50,\"focalPoint\":{\"x\":\"0.50\",\"y\":\"0.50\"},\"minHeight\":700,\"customGradient\":\"linear-gradient(135deg,rgb(2,3,129) 0%,rgb(45,98,229) 37%,rgba(86,162,238,0.59) 62%,rgba(82,121,188,0.35) 100%)\",\"isDark\":false,\"align\":\"full\"} -->\n<div class=\"wp-block-cover alignfull is-light\" style=\"min-height:700px\"><span aria-hidden=\"true\" class=\"wp-block-cover__background has-background-dim wp-block-cover__gradient-background has-background-gradient\" style=\"background:linear-gradient(135deg,rgb(2,3,129) 0%,rgb(45,98,229) 37%,rgba(86,162,238,0.59) 62%,rgba(82,121,188,0.35) 100%)\"></span><img class=\"wp-block-cover__image-background wp-image-361\" alt=\"\" src=\"http://localhost:8888/promart/wp-content/uploads/2022/07/promart-main-bg-scaled.jpeg\" style=\"object-position:50% 50%\" data-object-fit=\"cover\" data-object-position=\"50% 50%\"/><div class=\"wp-block-cover__inner-container\"><!-- wp:spacer {\"height\":\"80px\"} -->\n<div style=\"height:80px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":1,\"textColor\":\"nv-light-bg\"} -->\n<h1 class=\"has-text-align-center has-nv-light-bg-color has-text-color\">Водные спасательные средства</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"typography\":{\"fontSize\":17}},\"textColor\":\"nv-light-bg\"} -->\n<p class=\"has-text-align-center has-nv-light-bg-color has-text-color\" style=\"font-size:17px\">Programmatically work but low hanging fruit so new economy cross-pollination. Quick sync new <br>economy onward and upward.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons {\"layout\":{\"type\":\"flex\",\"justifyContent\":\"center\",\"orientation\":\"horizontal\"}} -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"className\":\"is-style-primary\"} -->\n<div class=\"wp-block-button is-style-primary\"><a class=\"wp-block-button__link\" href=\"/promart/spasatelnye-zhilety/\">Смотреть каталог</a></div>\n<!-- /wp:button -->\n\n<!-- wp:button {\"textColor\":\"nv-light-bg\",\"className\":\"is-style-secondary\"} -->\n<div class=\"wp-block-button is-style-secondary\"><a class=\"wp-block-button__link has-nv-light-bg-color has-text-color\" href=\"http://localhost:8888/promart/web-agency-gb-contact-us/\">Контакты</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:spacer {\"height\":\"80px\"} -->\n<div style=\"height:80px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:spacer {\"height\":\"80px\"} -->\n<div style=\"height:80px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div></div>\n<!-- /wp:cover -->\n\n<!-- wp:spacer {\"height\":\"80px\"} -->\n<div style=\"height:80px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":20,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"className\":\"icon-style is-style-rounded\"} -->\n<figure class=\"wp-block-image aligncenter size-large icon-style is-style-rounded\"><img src=\"http://localhost:8888/promart/wp-content/uploads/2020/01/neve-webdesign-GB-03.png\" alt=\"\" class=\"wp-image-20\" title=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":3,\"textColor\":\"neve-text-color\",\"className\":\"has-text-align-center\"} -->\n<h3 class=\"has-text-align-center has-neve-text-color-color has-text-color\">Продукция сертифицирована</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Сертификат ГИМС № РОСС EG.ME04.H01181.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"20px\"} -->\n<div style=\"height:20px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":21,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"className\":\"icon-style is-style-rounded\"} -->\n<figure class=\"wp-block-image aligncenter size-large icon-style is-style-rounded\"><img src=\"http://localhost:8888/promart/wp-content/uploads/2020/01/neve-webdesign-GB-04.png\" alt=\"\" class=\"wp-image-21\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":3,\"textColor\":\"neve-text-color\",\"className\":\"has-text-align-center\"} -->\n<h3 class=\"has-text-align-center has-neve-text-color-color has-text-color\">Более 10 лет на рынке</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Производим спасательные средства с 2000 года, имеем сотни частных и корпоративных клиентов.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"20px\"} -->\n<div style=\"height:20px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":18,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"className\":\"icon-style is-style-rounded\"} -->\n<figure class=\"wp-block-image aligncenter size-large icon-style is-style-rounded\"><img src=\"http://localhost:8888/promart/wp-content/uploads/2020/01/neve-webdesign-GB-01.png\" alt=\"\" class=\"wp-image-18\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":3,\"textColor\":\"neve-text-color\",\"className\":\"has-text-align-center\"} -->\n<h3 class=\"has-text-align-center has-neve-text-color-color has-text-color\">Доставляем по всей России</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Доставляем товары в любую точку РФ на договорных условия